products = {}
product = ''


class Content(BoxLayout):
    pass


class MD3Card(MDCard):
    source = StringProperty()  # путь к главному изображению карточки
    text = StringProperty()  # текст карточки
    under = StringProperty()  # текст карточки

class MD3Cardsmall(MDCard):
    name = StringProperty()

class Recipe(Screen):
    pass


class Test(MDApp):
    dialog = None

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.screen = Builder.load_string(KV)

    def openscreen(self):
        if len(productchange) != 0:
            self.root.ids.title.text = productchange
            self.root.ids.recipe.text = '\n'.join(rec[productchange][2])
            self.root.ids.fotka.source = f'images/{rec[productchange][0]}'
            for i in rec[productchange][1]:
                self.root.ids.indig.add_widget(
                    MD3Cardsmall(name=f'{i} - {rec[productchange][1][i]}'))
        else:
            self.root.ids.title.text = 'Выберите рецепт из списка рецептов.'

    def vibor(self, arg):
        global productchange
        productchange = arg
        products['Время'] = productchange

    def build(self):
        self.theme_cls.material_style = "M3"
        self.theme_cls.theme_style = "Light"
        return self.screen

    def indigrid(self):
        self.root.ids.container.clear_widgets()
        to_print = set()
        for i in ing.keys():
            if len(self.screen.ids.pole.text.lower()) != 0 and self.screen.ids.pole.text.lower() == i[
                                                                                                    :len(
                                                                                                        self.screen.ids.pole.text)].lower():
                to_print.add(i.capitalize())
        if len(to_print) == 0:
            pass
        for i in to_print:
            self.root.ids.container.add_widget(
                OneLineListItem(text=i, on_release=self.dobavka, theme_text_color="Custom", divider_color="green"))

    def dobavka(self, arg):
        global product
        product = arg.text
        if not self.dialog:
            self.dialog = MDDialog(
                title="Вводите количество ингридиента.",
                type="custom",
                content_cls=Content(),
                buttons=[
                    MDRaisedButton(
                        text="ОТМЕНА",
                        on_press=self.zakritie,
                        md_bg_color="#173B1E"
                    ),
                    MDRaisedButton(
                        text="ДОБАВИТЬ",
                        on_press=self.final,
                        md_bg_color="#173B1E"
                    ),
                ],
            )
        self.dialog.content_cls.ids.texts.text = ing[arg.text]
        self.dialog.content_cls.ids.gramms.text = ''
        self.dialog.open()

    def final(self, arg):
        self.dialog.dismiss(force=True)
        products[product] = self.dialog.content_cls.ids.gramms.text
        self.root.ids.list_products.clear_widgets()
        for i in products.keys():
            make = morph.parse(ing[i])[0]
            self.root.ids.list_products.add_widget(
                OneLineListItem(text=f'{i} - {products[i]} {make.make_agree_with_number(int(products[i])).word}',
                                on_release=self.udalenie,
                                theme_text_color="Custom", divider_color="green"))

    def recipe(self):
        glist = list()
        setofproduct = set(products.keys())
        self.root.ids.box.clear_widgets()
        for i in rec.keys():
            setofindeg = set(rec[i][1].keys())
            newset = setofproduct & setofindeg
            if len(newset) != 0:
                arr = list(newset)
                newset.clear()
                for j in range(len(arr)):
                    if (rec[i][1][arr[j]].split()[0]).isdigit() and int(products[arr[j]].split()[0]) >= int(
                            rec[i][1][arr[j]].split()[0]):
                        newset.add(arr[j])
            glist.append([newset, i, f'images/{rec[i][0]}'])
        glist.sort(key=lambda x: len(x[0]), reverse=1)
        for i in glist:
            self.root.ids.box.add_widget(
                MD3Card(
                    source=i[2],
                    text=i[1],
                    under=f'Совпадений: {len(i[0])}'
                )
            )

    def udalenie(self, arg):
        nazv = arg.text.split(' - ')
        del products[nazv[0]]
        self.root.ids.list_products.clear_widgets()
        for i in products.keys():
            self.root.ids.list_products.add_widget(
                OneLineListItem(text=f'{i} - {products[i]}', on_release=self.udalenie,
                                theme_text_color="Custom", divider_color="green"))

    def zakritie(self, arg):
        self.dialog.dismiss(force=True)


    def poluchenie(self):
        self.root.ids.textslider.text = str(self.root.ids.slider.value)

    def clear(self):
        self.screen.ids.pole.text = ''



Test().run()
